mapper.select:
SELECT dish.*,element.*,`type`.meal_type_name as typeName FROM diet_dish dish LEFT JOIN diet_element element ON dish.id = element.goal_id JOIN his_meal_type `type` ON `type`.id = dish.type_id
and dish.`name` like concat(concat('%',#{condition.name}),'%')
and dish.type_id = #{condition.typeId}
and `type`.cafeteria_id = #{condition.cafeteriaId}
and dish.status = 1 and element.goal_type = 2
ORDER BY ${sort} ${order} LIMIT #{from}, #{size}

SELECT COUNT(1) FROM diet_dish dish LEFT JOIN diet_element element ON dish.id = element.goal_id JOIN his_meal_type `type` ON `type`.id = dish.type_id
and dish.`name` like concat(concat('%',#{condition.name}),'%')
and dish.type_id = #{condition.typeId}
and `type`.cafeteria_id = #{condition.cafeteriaId}
and dish.status = 1 and element.goal_type = 2
