mapper.insert:

  SELECT LAST_INSERT_ID()

insert into diet_meal_times (meal_time_name, meal_time_icon, sort, 
  cafeteria_id, create_time, create_by, 
  update_time, update_by, status
  )
values (#{mealTimeName,jdbcType=VARCHAR}, #{mealTimeIcon,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}, 
  #{cafeteriaId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{createBy,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}
  )

mapper.select:
SELECT

FROM
diet_meal_times


    cafeteria_id = #{condition.cafeteriaId}

  and status = 1

ORDER BY ${sort} ${order}
LIMIT #{from}, #{size}

SELECT COUNT(1) FROM diet_meal_times
cafeteria_id = #{condition.cafeteriaId}
and status = 1

SELECT times.*,schedual.exp_hour,schedual.exp_min,schedual.exp_time FROM diet_meal_times times LEFT JOIN sys_oven_schedual schedual ON times.id = schedual.`key`
#{id}
and status = 1

SELECT

FROM
diet_meal_times

  cafeteria_id = #{cafeteriaId}

  and id = #{mealId}

  and status = 1
