mapper.select:
SELECT patient.*,wallet.total_wallet,patient_orders.order_name, (case when oms.oms_type is null then 0 when oms.oms_type=1 or oms.oms_type=2 then 1 else 2 end) flag FROM his_patient patient LEFT JOIN his_patient_wallet wallet ON patient.patient_id = wallet.patient_id LEFT JOIN his_inpatient_area ward ON ward.ward_code = patient.ward_code LEFT JOIN his_relation relation ON ward.id = relation.ward_id LEFT JOIN (select patient_id,order_name from (SELECT * from his_patient_orders order by start_time desc) A group by A.patient_id) patient_orders ON patient.patient_id = patient_orders.patient_id LEFT JOIN (select * from his_oms where DATE_FORMAT(dining_time,'%Y-%m-%d') = #{condition.diningDate}) oms ON patient.patient_id = oms.patient_id
and ward.id = #{condition.wardId}
and relation.cafeteria_id = #{condition.cafeteriaId}
and patient.bed_no = #{condition.bedNo}
and patient.sex = #{condition.sex}
and patient.inp_no = #{condition.inpNo}
and patient.diagnosis like concat(concat('%',#{condition.mainJudge}),'%')
and ((DATE_FORMAT(patient.in_hos_date,'%Y-%m-%d') <= #{condition.diningDate} and patient.out_status=0) or (DATE_FORMAT(patient.in_hos_date,'%Y-%m-%d') <= #{condition.diningDate} and (DATE_FORMAT(patient.out_hos_date,'%Y-%m-%d') >= #{condition.diningDate} or patient.out_hos_date is null)))
and oms.oms_type is null
and (oms.oms_type = 1 or oms.oms_type=2)
and oms.oms_type = 3
GROUP BY patient.patient_id ORDER BY ${sort} ${order} LIMIT #{from}, #{size}

SELECT COUNT(1) FROM his_patient patient LEFT JOIN his_patient_wallet wallet ON patient.patient_id = wallet.patient_id LEFT JOIN his_inpatient_area ward ON ward.ward_code = patient.ward_code LEFT JOIN his_relation relation ON ward.id = relation.ward_id LEFT JOIN (select * from his_oms where DATE_FORMAT(dining_time,'%Y-%m-%d') = #{condition.diningDate}) oms ON patient.patient_id = oms.patient_id
and ward.id = #{condition.wardId}
and relation.cafeteria_id = #{condition.cafeteriaId}
and patient.bed_no = #{condition.bedNo}
and patient.sex = #{condition.sex}
and patient.inp_no = #{condition.inpNo}
and patient.diagnosis like concat(concat('%',#{condition.mainJudge}),'%')
and ((DATE_FORMAT(patient.in_hos_date,'%Y-%m-%d') <= #{condition.diningDate} and patient.out_status=0) or (DATE_FORMAT(patient.in_hos_date,'%Y-%m-%d') <= #{condition.diningDate} and (DATE_FORMAT(patient.out_hos_date,'%Y-%m-%d') >= #{condition.diningDate} or patient.out_hos_date is null)))
and oms.oms_type is not null
and (oms.oms_type = 1 or oms.oms_type=2)
and oms.oms_type = 3

SELECT patient.*,wallet.total_wallet,if(count(oms.id) = 0,0,1) as flag,DATE_FORMAT(oms.dining_time,'%Y-%m-%d') as dining_date,orders.order_name FROM his_patient patient LEFT JOIN his_patient_wallet wallet ON patient.patient_id = wallet.patient_id LEFT JOIN his_oms oms ON patient.patient_id = oms.patient_id LEFT JOIN his_inpatient_area ward ON ward.ward_code = patient.ward_code LEFT JOIN his_relation relation ON ward.id = relation.ward_id LEFT JOIN his_patient_orders orders ON patient.patient_id = orders.patient_id
#{id}
and relation.user_id = #{userId} and patient.out_status = 0 GROUP BY patient.patient_id
