mapper.select:
SELECT

FROM
sys_user


    and user_type = #{condition.userType}


    and cafeteria_id = #{condition.cafeteriaId}


    and user_name = #{condition.userName}


       and  `password` = #{condition.password}

  and status != 3

ORDER BY ${sort} ${order}
LIMIT #{from}, #{size}

SELECT COUNT(1)
FROM
sys_user


    and user_type = #{condition.userType}


    and cafeteria_id = #{condition.cafeteriaId}


      and user_name = #{condition.userName}


      and `password` = #{condition.password}

  and status != 3
